import HeaderScreen from "./HeaderScreen";
import FooterScreen from "./FooterScreen";
import { useSelector } from "react-redux";
import MessageBox from "./messageBox";
import { Form, Button, Container, Col, Row } from "react-bootstrap";
import { ContactAction } from "../actions/ContactAction";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import Alert from '@mui/material/Alert';
import { Link } from "react-router-dom";
import Grid from "@mui/material/Grid";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import FormGroup from '@mui/material/FormGroup';
import { Input } from '@mui/material';

export default function Contact3() {
  const dispatch = useDispatch();
  const contactDetails = useSelector((state) => state.contactSubmit);
  const { contactInfo, loading } = contactDetails;
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");
  const [emailError, setEmailError] = useState("");
  const [nameError, setNameError] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [verified, setVerified] = useState(false);

  function mobileValidation(value) {
    const re = /^[0-9\b]+$/;

    if (value.length === 10) {
      if ((value === "") | re.test(value)) {
        setPhoneError("");
        setPhone(value);
      }
    } else {
      if ((value === "") | re.test(value)) {
        setPhone(value);
        setPhoneError("Invalid mobile input!");
      }
    }
  }
  const submitContact = (e) => {
    const re =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;

    if (name == "" || name.length > 120) {
      setNameError("Enter a valid name!");
      return false;
    } else {
      setNameError("");
      if (re.test(email)) {
        setEmailError("");

        if (phone.length == 10) {
          setPhoneError("");
          console.log("contact", name, email, phone, message);
          if (setVerified) {
            dispatch(ContactAction(name, email, phone, message));
          }
        } else {
          setPhoneError("Enter valid mobile number!");
        }
      } else {
        setEmailError("Invalid Email Address");
      }
    }
  };

  useEffect(() => {
    window.scrollTo(0, 0);

    if (loading) {
    } else if (contactInfo != null) {
      if (contactInfo.response_code == 1) {
        setName("");
        setEmail("");
        setPhone("");
        setMessage("");
      }
    }
  }, [contactInfo]);
  //console.log("response code", contactInfo);
  return (
    <>
      <HeaderScreen />
      <section className="page-hero home is-white">
        <div className="bg"></div>
        <div className="hero-content" id="scene-wrapper--hero-wave">
          <Grid container>
            <Grid>
              <br />
            </Grid>

<Grid container >

              <Grid item lg={6} sm={6} xs={12} sx={{padding:"20px"}} >
                <h3>We're here to support you. If you're looking for help you've come to the right place.</h3>
                <p>Please show us your interest by filling the form.</p>
              </Grid>

              <Grid item lg={6} sm={6} xs={12} 

                style={{ backgroundColor: "white", borderRadius: "20px" ,padding:"20px" }}
              >
                <br />
                <h3 style={{ color: "black" }}>Contact Details</h3>
                <p style={{ color: "black" }}>
                  Enter your{" "}
                  <span style={{ color: "green" }}>
                    {" "}
                    details so that we can{" "}
                  </span>
                  contact you!
                </p>
                <br/>
                <Card>
                  <CardContent>

                      <Grid className="mb-3">

                      </Grid>
                      <FormGroup className="mb-3" controlId="formBasicEmail">
                      {loading ? (
                      ""
                    ) : contactInfo == null ? (
                      ""
                    ) : contactInfo.response_code == 1 ? (
                      <center>
                        {" "}
                        <Alert severity="success">Successfully submitted!</Alert>
                      </center>
                    ) : contactInfo.response_code == 0 ? (
                      <Alert severity="error">Email already exists!</Alert>
                    ) : (
                      ""
                    )}  
                    <Grid>
                        <FormLabel style={{ color: "black", marginBottom:'0.5 rem' }}>
                          Enter your name{" "}
                          <span className="required-span">*</span>
                        </FormLabel>
                        </Grid>

                        <Grid>
                        <FormControl>
                          <Input id="my-input" className="form-control" value={name} 
                          onChange={(e) => setName(e.target.value)}
                          placeholder="Enter your full name" aria-describedby="my-helper-text"/>
                           </FormControl>
                        <span style={{ color: "red" }}>{nameError}</span></Grid>

                      </FormGroup>
                      <FormGroup className="mb-3" controlId="formBasicEmail">
                        <FormLabel style={{ color: "black" }}>
                          Enter email address{" "}
                          <span className="required-span">*</span>
                        </FormLabel>

                        <FormControl/>
                        <Input
                          className="form-control"
                          type="text"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                          placeholder="Enter your email address"
                          required />
                        <FormControl/>
                        <span style={{ color: "red" }}>{emailError}</span>

                      </FormGroup>

                      <FormGroup className="mb-3" controlId="formBasicEmail">
                        <FormLabel style={{ color: "black" }}>
                          Enter Mobile no.
                          <span className="required-span">*</span>
                        </FormLabel>
                        <FormControl><Input
                          className="form-control"
                          className="phn-num"
                          type="text"
                          id=""
                          name=""
                          maxLength={10}
                          min="0"
                          value={phone}
                          onChange={(e) => mobileValidation(e.target.value)}
                          placeholder="9179892147"
                          name=""
                        /> 
                        </FormControl>
                        <span style={{ color: "red" }}>{phoneError}</span>

                      </FormGroup>
                      <FormGroup className="mb-3" controlId="formBasicEmail">
                        <FormLabel style={{ color: "black" }}>
                          Message:-
                        </FormLabel>
                        <FormControl ><Input
                          className="form-control"
                          type="text"
                          value={message}
                          onChange={(e) => setMessage(e.target.value)}
                          placeholder="type here..."
                        /></FormControl>
                      </FormGroup>
                      <br />
                      <center>
                        {" "}
                        <Button variant="primary"
                         onClick={(e) => submitContact(e)}
                         disabled={loading ? "disabled" : ""}
                        > 
                        {loading
                          ? "Please wait submitting your details.."
                          : "Submit your details"}
                          </Button>{" "}
                      </center>
                      <br />

                  </CardContent>
                </Card>
              </Grid>


</Grid>
