import HeaderScreen from "./HeaderScreen";
import FooterScreen from "./FooterScreen";
import { useSelector } from "react-redux";
import MessageBox from "./messageBox";
//import { Form, Button, Col, Row, Card } from "react-bootstrap";
import Container from "@mui/material/Container";
import { ContactAction } from "../actions/ContactAction";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import Alert from "@mui/material/Alert";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import { InputBase } from "@mui/material";

export default function Contact2() {
  const dispatch = useDispatch();
  const contactDetails = useSelector((state) => state.contactSubmit);
  const { contactInfo, loading } = contactDetails;
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");
  const [emailError, setEmailError] = useState("");
  const [nameError, setNameError] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [verified, setVerified] = useState(false);

  function mobileValidation(value) {
    const re = /^[0-9\b]+$/;

    if (value.length === 10) {
      if ((value === "") | re.test(value)) {
        setPhoneError("");
        setPhone(value);
      }
    } else {
      if ((value === "") | re.test(value)) {
        setPhone(value);
        setPhoneError("Invalid mobile input!");
      }
    }
  }
  const submitContact = (e) => {
    const re =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;

    if (name == "" || name.length > 120) {
      setNameError("Enter a valid name!");
      return false;
    } else {
      setNameError("");
      if (re.test(email)) {
        setEmailError("");

        if (phone.length == 10) {
          setPhoneError("");
          console.log("contact", name, email, phone, message);
          if (setVerified) {
            dispatch(ContactAction(name, email, phone, message));
          }
        } else {
          setPhoneError("Enter valid mobile number!");
        }
      } else {
        setEmailError("Invalid Email Address");
      }
    }
  };
  useEffect(() => {
    window.scrollTo(0, 0);

    if (loading) {
    } else if (contactInfo != null) {
      if (contactInfo.response_code == 1) {
        setName("");
        setEmail("");
        setPhone("");
        setMessage("");
      }
    }
  }, [contactInfo]);
  //console.log("response code", contactInfo);
  console.log("name", setName);
  return (
    <>
      <HeaderScreen />
      <section className="page-hero home is-white">
        <div className="bg"></div>
        <div className="hero-content" id="scene-wrapper--hero-wave">
          <Grid>
            <Card
              style={{
                maxWidth: 450,
                padding: "20px 5px",
                marginLeft: "750px",
                borderRadius: "5px",
              }}
            >
              <Typography gutterBottom variant="h3" align="center">
                Contact-mui
              </Typography>
              <CardContent>
                <Typography gutterBottom variant="h5">
                  Contact Us
                </Typography>
                <Typography
                  variant="body2"
                  color="textSecondary"
                  component="p"
                  gutterBottom
                >
                  Fill up the form and our team will get back to you within 24
                  hours.
                </Typography>
                <form>
                  <Grid container spacing={1}>
                    <Grid xs={12} item>
                      <label>Enter your full name</label>&nbsp;
                      <span style={{ color: "red" }}>*</span>
                      <InputBase
                        className="inputbase"
                        placeholder="Enter first name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        fullWidth
                      />
                      <span style={{ color: "red" }}>{nameError}</span>
                    </Grid>
                    <Grid item xs={12}>
                      <label>Enter email address</label>&nbsp;
                      <span style={{ color: "red" }}>*</span>
                      <InputBase
                        className="inputbase"
                        placeholder="Enter your email address"
                        fullWidth
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                      />
                     <span style={{ color: "red" }}>{emailError}</span>
                    </Grid>
                    <Grid item xs={12}>
                      <label>Enter your phone number</label>&nbsp;
                      <span style={{ color: "red" }}>*</span>
                      <InputBase
                      maxLength={10}
                      value={phone}
                      onChange={(e) => mobileValidation(e.target.value)}
                        className="inputbase"
                        placeholder="9176524924"
                        fullWidth
                      />
                      <span style={{ color: "red" }}>{phoneError}</span>
                    </Grid>
                    <Grid item xs={12}>
                      <label>Message</label>
                      <InputBase
                      value={message}
                      onChange={(e) => setMessage(e.target.value)}
                        className="inputbase"
                        placeholder="Type here..."
                        fullWidth
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <Button
                        type="submit"
                        onClick={(e) => submitContact(e)}
                        disabled={loading ? "disabled" : ""}
                        variant="contained"
                        color="primary"
                        fullWidth
                        style={{marginTop:"30px"}}
                      >
                         {loading
                          ? "Please wait submitting your details.."
                          : "Submit your details"}
                      </Button>


                    </Grid>
                  </Grid>
                </form>
              </CardContent>
            </Card>
          </Grid>

          <Grid container >
                        <Grid item xs={7}>
                        </Grid>
                        <Grid item xs={3} sx={{backgroundColor:"white", borderRadius:"20px"}}>
                          <label style={{color:'black',padding:"30px"}}>Enter your name</label>
                          <br/>
                          <input type="text" style={{width:"360px", color:'black'}}/>
                        </Grid>
                         <Grid item xs={2}>
                        </Grid>
                      </Grid>
          <div className="col-md-6">
            <div className="hero-img" data-appear-fade-in>
              <div className="holder" style={{ maxWidth: "700px" }}>
                <img
                  src="data:image/svg+xml,%3Csvg%20xmlns='http://www.w3.org/2000/svg'%20viewBox='0%200%200%200'%3E%3C/svg%3E"
                  className="attachment-xl"
                  alt="Homepage_2x"
                  data-lazy-src="wp-content/uploads/2022/02/Homepage_2x.png"
                />
                <noscript>
                  <img
                    src="wp-content/uploads/2022/02/Homepage_2x.png"
                    className="attachment-xl"
                    alt="Homepage_2x"
                  />
                </noscript>
              </div>
            </div>
          </div>
        </div>
      </section>
      <FooterScreen />
    </>
  );
}
