//contact no. lim 10 digit
//message text area lim 500 char
// submit button
import { Form, Button, Container, Col, Row, Card } from "react-bootstrap";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import FooterScreen from "./FooterScreen";
//import { ContactAction } from "../actions/ContactAction";
//import MessageBox from "./messageBox";
//import ReCAPTCHA from "react-google-recaptcha";
import HeaderScreen from "./HeaderScreen";


export default function Contactscreen() {
  const dispatch = useDispatch();
  const contactDetails = useSelector((state) => state.contactSubmit);
  const { contactInfo, loading } = contactDetails;
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [message, setMessage] = useState("");
  const [emailError, setEmailError] = useState("");
  const [nameError, setNameError] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [verified,setVerified]=useState(false)
  function onChange(value){
    console.log("Captcha value",value)
    setVerified(true)
  }

  function mobileValidation(value) {
    const re = /^[0-9\b]+$/;

    if (value.length === 10) {
      if ((value === "") | re.test(value)) {
        setPhoneError("");
        setPhone(value);
      }
    } else {
      if ((value === "") | re.test(value)) {
        setPhone(value);
        setPhoneError("Invalid mobile input!");
      }
    }
  }
  const submitContact = (e) => {
    const re =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;

    if (name == "" || name.length > 120) {
      setNameError("Enter a valid name!");
      return false;
    } else {
      setNameError("");
      if (re.test(email)) {
        setEmailError("");

        if (phone.length == 10) {
          setPhoneError("");
          console.log("contact", name, email, phone, message);
          if(setVerified){
          // dispatch(ContactAction(name, email, phone, message));
        }
        } else {
          setPhoneError("Enter valid mobile number!");
        }
      } else {
        setEmailError("Invalid Email Address");
      }
    }
  };

  useEffect(() => {
    window.scrollTo(0, 0);

    if (loading) {
    } else if (contactInfo != null) {
      if (contactInfo.response_code == 1) {
        setName("");
        setEmail("");
        setPhone("");
        setMessage("");
      }
    }
  }, [contactInfo]);
  console.log("response code", contactInfo);
  return (
    <>
     <HeaderScreen />
      <Container>
        <Row>
          <br />
        </Row>

        <Row>
          <Col lg="12" className="core-features">
            <img src="https://www.nxgsols.com/img/svg/section-icon-2.svg" />
            <h1 style={{ padding: "13px" }}>Contact </h1>
          </Col>
        </Row>

        <Row>
          <Col lg="3"></Col>

          <Col sm="12" lg="6">
            <br />
            <h3>Contact Details</h3>
            <p>
              Enter your details <span>so that</span> we can contact you!
            </p>
            <Card>
              <Card.Body>
                <Form>
                  <Row className="mb-3">
                    <Form.Group as={Col} md="12">
                      {/* Not in use */}
                    </Form.Group>
                  </Row>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    {loading ? (
                      ""
                    ) : contactInfo == null ? (
                      ""
                    ) : contactInfo.response_code == 1 ? (
                      <center>
                        {" "}
                        {/* <MessageBox
                          msg="Submitted successfully!"
                          alert="success"
                        ></MessageBox> */}
                      </center>
                    ) : contactInfo.response_code == 0 ? (
                      <MessageBox
                        msg="Email Address already in use!"
                        alert="danger"
                      ></MessageBox>
                    ) : (
                      ""
                    )}

                    <Form.Label>
                      Enter your name <span className="required-span">*</span>
                    </Form.Label>
                    <Form.Control
                      type="text"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      placeholder="Enter your full name"
                      required
                    />
                    <span style={{ color: "red" }}>{nameError}</span>
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>
                      Enter email address{" "}
                      <span className="required-span">*</span>
                    </Form.Label>
                    <Form.Control
                      type="text"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Enter your email address"
                      required
                    />
                    <span style={{ color: "red" }}>{emailError}</span>
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>
                      Enter Mobile no.<span className="required-span">*</span>
                    </Form.Label>
                    <Form.Control
                      className="phn-num"
                      type="text"
                      id=""
                      name=""
                      maxLength={10}
                      min="0"
                      value={phone}
                      onChange={(e) => mobileValidation(e.target.value)}
                      placeholder="9179******"
                      name=""
                    />
                    <span style={{ color: "red" }}>{phoneError}</span>
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Message:-</Form.Label>
                    <Form.Control
                      type="input"
                      value={message}
                      onChange={(e) => setMessage(e.target.value)}
                      placeholder="type here..."
                    />
                  </Form.Group>
                  <br />
                {/* <center>  <ReCAPTCHA

        sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
        onChange={onChange}
      /></center>  <br/> */}
                  <center>
                    {" "}
                    <Button
                      onClick={(e) => submitContact(e)}
                      variant="primary"
                      disabled={loading ? "disabled" : ""}
                    >
                      {loading
                        ? "Please wait submitting your details.."
                        : "Submit your details"}
                    </Button>{" "}
                  </center>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row>
          <br /> <br />
        </Row>
        <Row>
          <Col lg="6" sm="12">
            <div className="hero-section-2">
              <h2>
                We're here to support you. If you're looking for help you've
                come to the right place.
              </h2>
              <p>
                In 2014, NXG Solutions pioneered and envisioned next generations
                smart robotics systems with an ambition to make human life
                better and healthier through a sustainable robotics and computer
                vision. Finally, in 2017 NXG Solution founded in Alpharetta,
                Atlanta Metropolitan in USA with vision and mission to build
                innovative products for mankind to ease their life in three key
                areas Robotics, Artificial Intelligence and Digital space by
                applying next generations technologies.
              </p>
              <p>
                Due to increasing customer demands and economical solutions &
                services, NXG Solutions open its delivery center in the
                Chattisgath State Capital City Raipur in India.
              </p>
            </div>
          </Col>
          <Col lg="6" sm="12">
            <div className="hero-image-section-2">
              <img
                className="hero-image"
                src="https://www.nxgsols.com/img/svg/about1.svg"
              />
            </div>
          </Col>
        </Row>
      </Container>
      <FooterScreen></FooterScreen>
    </>
  );
}
