import HeaderScreen from "./HeaderScreen";
import * as React from 'react';
import { Form, Button, Container, Col, InputGroup, Row } from "react-bootstrap";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Navigate } from "react-router-dom";
import { RegisterAction } from "../actions/RegisterAction";
import { useSelector } from "react-redux";
import FooterScreen from "./FooterScreen";
import MessageBox from "./messageBox";
import Alert from "@mui/material/Alert";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import FormLabel from "@mui/material/FormLabel";
import FormControl from "@mui/material/FormControl";
import FormGroup from "@mui/material/FormGroup";
import { Input } from "@mui/material";
import { FormHelperText } from "@mui/material";
import Select from '@mui/material/Select';
import { useTheme } from '@mui/material/styles';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const names = [
  'Oliver Hansen',
  'Van Henry',
  'April Tucker',
  'Ralph Hubbard',
  'Omar Alexander',
  'Carlos Abbott',
  'Miriam Wagner',
  'Bradley Wilkerson',
  'Virginia Andrews',
  'Kelly Snyder',
];

function getStyles(name, personName, theme) {
  return {
    fontWeight:
      personName.indexOf(name) === -1
        ? theme.typography.fontWeightRegular
        : theme.typography.fontWeightMedium,
  };
}

export default function RegisterScreen() {
  const dispatch = useDispatch();
  const sellerDetails = useSelector((state) => state.sellerRegister);
  const { sellerInfo, loading, error } = sellerDetails;
  const [email, setName] = useState("");
  const [password, setPassword] = useState("");
  const [storUrl, setUrl] = useState("");
  const [passError, setPassError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [urlError, setUrlError] = useState("");
  const [urlValidate, setValidate] = useState(false);
  const [category, setCategory] = useState();
  const [categoryError, setCategoryError] = useState();

  const validateUrl = (e) => {
    var re = /[^a-zA-Z0-9\-]/;
    if (e.match(/^[a-z0-9][a-z0-9\-]*[a-z0-9]$/)) {
      setUrlError("");
      setValidate(true);
      setUrl(e);
      //dispatch(checkurl(storUrl))
    } else {
      setUrl(e);
      setValidate(false);
      setUrlError("please enter a valid Url!");
    }
  };

  const handelSubmit = (e) => {
    const re =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    if (category == "" || category == undefined) {
      setCategoryError("Category is required");
      return false;
    } else {
      setCategoryError("");
      if (re.test(email)) {
        setEmailError("");

        if (password.length > 8) {
          setPassError("");
          if (urlValidate == true) {
            console.log("data", email, password, storUrl);
            dispatch(RegisterAction(email, password, storUrl, category));
          }
        } else {
          setPassError("Password must be contain minimum 8 characters");
        }
      } else {
        setEmailError("Invalid Email Address");
      }
    }
  };
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

    const theme = useTheme();
    const [personName, setPersonName] = React.useState([]);

    const handleChange = (event) => {
      const {
        target: { value },
      } = event;
      setPersonName(
        // On autofill we get a stringified value.
        typeof value === 'string' ? value.split(',') : value,
      );
    };
  return (
    <>
      {loading ? (
        ""
